import torch, torch.nn.functional as F

def entropy(p):
    return -(p * p.log()).sum(1)

class CWTentAdapter:
    def __init__(self, model, lr=1e-3, momentum=0.9):
        self.model = model.eval()
        self.model.requires_grad_(False)
        # enable gradients for affine BN params only
        for m in model.modules():
            if isinstance(m, torch.nn.BatchNorm2d):
                m.weight.requires_grad_(True)
                m.bias.requires_grad_(True)
        self.optimizer = torch.optim.SGD(filter(lambda p: p.requires_grad, model.parameters()), lr=lr, momentum=momentum)

    @torch.no_grad()
    def predict(self, x):
        return self.model(x).softmax(1)

    def adapt(self, x):
        self.model.train()        # BN uses batch statistics
        self.optimizer.zero_grad()
        logits = self.model(x)
        probs = logits.softmax(1)
        H = entropy(probs)
        w = 1 - H / torch.log(torch.tensor(probs.size(1), device=x.device))
        loss = (w * H).sum() / w.sum()
        loss.backward()
        self.optimizer.step()
        self.model.eval()
        return probs.detach()

